export default function Heart({foto}) {
  const {fotos, setFotos} = useContext(MyContext)

  const setFavorito = (id) =>{
    const fotoConClick = fotos.findIndex((f)=> f.id === id);
    fotos[fotoConClick].liked = !fotos[fotoConClick].liked;
    setFotos([...fotos]);
}
  return (
    <>
      <svg width="40px" viewBox="0 0 24 24"  style={{ position:'absolute', bottom: '80%', left: '85%'}}>
        <path
          onClick = {()=> setFavorito(foto.id)} variant={foto.liked?'danger':'success'}
          d="M12,21.35L10.55,20.03C5.4,15.36 2,12.27 2,8.5C2,5.41 4.42,3 7.5,3C9.24,3 10.91,3.81 12,5.08C13.09,3.81 14.76,3 16.5,3C19.58,3 22,5.41 22,8.5C22,12.27 18.6,15.36 13.45,20.03L12,21.35Z"
        />
      </svg>
    </>
  );
}
